//requirements
const express = require('express');
const { Liquid } = require('liquidjs');

//app setup
const app = express();
const server = require('http').createServer(app);
const io = require('socket.io')(server);
const engine = new Liquid();
app.engine('liquid', engine.express());
app.set('views', './views');
app.set('view engine', 'liquid');

let userCerts = [];

dummyCert = {
  id: "A-100",
  metadata: {
    cred: "",
    issued: "",
    expiry: "",
    body: "",
    notes:""
  },
  image: "data:image/jpeg;base64, ",
}

const newCert = () => {
  return { 
    id:"", 
    metadata: { cred:"", issued:"", expiry:"", body:"", notes:""},
    image:"",
  } 
}
const findCertIndex = (id) => userCerts.findIndex(obj => obj.id?.$id === id);
const findCert = (id) => userCerts[findCertIndex(id)];
const moveCert = (id, dir) => {
  const newOrder = [...userCerts];
  const index = findCertIndex(id);
  newOrder.splice(index, 1); 
  newOrder.splice(index + dir, 0, userCerts[index]);
  return newOrder;
}


//---Socket interaction
//Main pathway
app.get('/build-document', (req, res) => {
  res.render('frontend.liquid', {name: req.query.name, email: req.query.email});

});

//--- Webserver

// Socket Listening
io.on('connection', (socket, req) => {

  socket.on('get-certs', () => {
    io.emit('update', userCerts);
  });

  socket.on('add-dummy-cert', () => {
    userCerts.push(dummyCert);
    io.emit('update', userCerts);
  });

  socket.on('edit-cert', (data) => {
    var newCert = newCert();
    io.emit('update', userCerts);
  });

  socket.on('move-cert-dir', ({cert, value}) => {
    moveCert();
    io.emit('update', userCerts);
  });
});

// Add error handling middleware
app.use((err, req, res, next) => {
  console.error(err);
  res.status(500).send('Server error');
});

// Spin up a server
app.use(express.static('public'));
const port = 3000;
server.listen(port, () => {
  console.log(`Server started on port ${port}`);
});